import requests
import json
from time import time
from uuid import uuid4


def retrieveAvailableBalance(subId, consentToken, accountNo):
    url = f"https://sandbox-apis.bankofcyprus.com/df-boc-org-sb/sb/psd2/v1/accounts/{accountNo}/balance"

    payload = {}
    headers = {
        'Content-Type': 'application/json',
        'Authorization': f'Bearer {consentToken}',
        'subscriptionId': subId,
        'originUserId': '50520222',
        'journeyId': str(uuid4()),
        'timeStamp': str(int(time())),
        'x-client-certificate': ''
    }

    response = requests.request("GET", url, headers=headers, data=payload)

    return response.json()
